    /**
     * 清除缓存
     *
     * @param key 键
     * @return 值
     */
    public static Set<String> clearKeys(String pattern) {
        Set<String> keys = null;
        Jedis jedis = null;
        try {
            jedis = getResource();
            keys = jedis.keys(pattern);
            if (keys != null && keys.size() > 0) {
                jedis.del(new String[keys.size()]);
            }
        } catch (Exception e) {
            logger.warn("pattern {} = {}", pattern);
        } finally {
            returnResource(jedis);
        }
        return keys;
    }

