    /**
     * 根据配置文件单节点还是哨兵获取资源
     *
     * @return Jedis jedis实例
     * @throws JedisException jedis异常
     */
    public static Jedis getResource() throws JedisException {
        Jedis jedis = null;
        try {
            // 哨兵类型
            if (redisType == RedisType.SENTINEL.ordinal()) {
                Set<String> sentinels = new HashSet<>();
                String[] sentinelArray = sentinelHost.split(",");
                Collections.addAll(sentinels, sentinelArray);

                jedisPool = JedisSentinelFactory.getJedisPool(sentinelName, sentinels, c, 0, sentinelPassword);
                jedis = JedisSingleFactory.createResources();
            } else {
                // 单节点类型
                jedisPool = JedisSingleFactory.getJedisPool(c, host, port, 0, pass, db);
                jedis = JedisSingleFactory.createResources();
            }

        } catch (JedisException e) {
            logger.warn("getResource.", e);
            returnBrokenResource(jedis);
            throw e;
        }
        return jedis;
    }

    /**
     * 归还资源
     *
     * @param jedis
     * @param isBroken
     */
    public static void returnBrokenResource(Jedis jedis) {
        if (jedis != null) {
            jedisPool.returnBrokenResource(jedis);
        }
    }

    /**
     * 释放资源
     *
     * @param jedis
     * @param isBroken
     */
    public static void returnResource(Jedis jedis) {
        if (jedis != null) {
            jedisPool.returnResource(jedis);
        }
    }

    /**
     * 获取byte[]类型Key
     *
     * @param key
     * @return
     */
    public static byte[] getBytesKey(Object object) {
        if (object instanceof String) {
            return StrUtil.getBytes((String) object);
        } else {
            return ObjectUtils.serialize(object);
        }
    }

    /**
     * Object转换byte[]类型
     * 序列化对象
     *
     * @param key
     * @return
     */
    public static byte[] toBytes(Object object) {
        ObjectOutputStream oos = null;
        ByteArrayOutputStream baos = null;
        try {
            if (object != null) {
                baos = new ByteArrayOutputStream();
                oos = new ObjectOutputStream(baos);
                oos.writeObject(object);
                return baos.toByteArray();
            }
        } catch (Exception e) {
            e.printStackTrace();
        }
        return null;
    }

    /**
     * byte[]型转换Object
     * 反序列化对象
     *
     * @param key
     * @return
     */
    public static Object toObject(byte[] bytes) {
        ByteArrayInputStream bais = null;
        try {
            if (bytes != null && bytes.length > 0) {
                bais = new ByteArrayInputStream(bytes);
                ObjectInputStream ois = new ObjectInputStream(bais);
                return ois.readObject();
            }
        } catch (Exception e) {
            e.printStackTrace();
        }
        return null;
    }

